import React, { Component } from 'react';
import './App.css';
import Clarifai from 'clarifai';
import Navigation from './Components/Navigation/Navigation';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkFrom/ImageLinkForm';
import Rank from './Components/Rank/Rank';
import Prtls from './Components/Particles/Particles';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Result from './Components/Result/Result'    


      // const USER_ID = 'hadi_01';
      // const PAT = '9a0cf551bffe4b2ab6c4c5903d0a6e79';
      // const APP_ID = 'First-Application';
      //  const API_KEY =  'eec46ed0322e4dfab533a8a76f938c64';
      // const MODEL_ID = 'color-recognition';
      // const MODEL_VERSION_ID = 'dd9458324b4b45c2be1a7ba84d27cd04';    // const IMAGE_URL = ' ';
      // const IMAGE_URL = 'https://samples.clarifai.com/metro-north.jpg';

      const raw = new Clarifai.App({
        apiKey: 'eec46ed0322e4dfab533a8a76f938c64'
       });

class  App extends Component {
  constructor(){
    super();
    this.state={
      input: '',
      IMAGEURL: '',
      Data: '',
    }
   }
   Display=(data)=>{
    const recog = data.outputs[0].data.regions[0].data.concepts[0].name;
    return {
      recognition : recog
    }
   }
   DisplayResult = (Data)=> {
    // console.log(Data)
    this.setState({Data: Data})
    // const concepts = data.outputs[0].data.regions[0].data.concepts[0].value;
    // const concepts1 = data.outputs[0].data.regions[0].data.concepts[0].name;
    // console.log( "There is  " , concepts , "% probability, that its picture of " , concepts1 );
   }
   onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  onTap = ()=>{
    this.setState({IMAGEURL: this.state.input});
    raw.models
      .predict(  Clarifai.CELEBRITY_MODEL ,this.state.input)
    .then(response =>  this.DisplayResult(this.Display(response)))
      // console.log(response.outputs[0].data.concepts)
      .catch(err=> console.log("error" , err));

    }

  render() {
    return (

      <div className="App">
        <Prtls className="particles"/>
      <Navigation />
       <Logo />
       <Rank />
      <ImageLinkForm 
      onInputChange={this.onInputChange} 
      onTap={this.onTap} />
      <FaceRecognition Data={this.state.Data} IMAGEURL={this.state.IMAGEURL} />
      <Result Data={this.state.Data} />
    </div>
  );
}
}

export default App;
