import React from "react";
class  Register extends React.Component  {
  constructor(props){
    super(props);
      this.state={
      email: '',
      Password: '',
      name: '',
    }
  }

onNameChange=(event)=>{
  this.setState({name: event.target.value})
}
onEmailChange=(event)=>{
  this.setState({email: event.target.value})
}
onPasswordChange=(event)=>{
  this.setState({Password: event.target.value})
}
  render(){
    const { onRouteChange} = this.props

    return (
      <div>


   <article className="hide-child relative br3 ba b--black-20 shadow-5 mw6 center">

        <main className="pa2 black-80">
  <div className="measure ">
    <fieldset  className="ba b--transparent ph0 mh0">
    <legend className="f2 fw6 ph0 mh0">Register</legend>

    <div className="mt3">
    <label className="db fw6 lh-copy f6" >Name</label>
    <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="Text" name="Name"  placeholder="Enter Name"/>
    </div>

    <div className="mt3">
    <label className="db fw6 lh-copy f6" >Email</label>
    <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  placeholder=" Enter Email-Address"/>
    </div>


    <div className="mv3">
    <label className="db fw6 lh-copy f6" >Password</label>
        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  placeholder="Enter Password" autoComplete="tel"/>
        </div>
        </fieldset>

        <div className="">
        <input 
        onClick={ () => onRouteChange ('home')}
        className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f5 dib" 
        type="submit" 
        value="Register"/>
        </div>
        </div>
        </main>
        </article> 

        </div>
        );
    }
      }

export default Register;
